```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string wordToFind;
    std::cout << "Enter the word to find: ";
    std::cin >> wordToFind;

    std::ifstream file("file.txt");
    if (!file) {
        std::cout << "Unable to open file";
        return 1; // terminate with error
    }

    std::string word;
    int count = 0;
    while (file >> word) {
        if (word == wordToFind) {
            count++;
        }
    }

    std::cout << "The word '" << wordToFind << "' appears " << count << " times in the file." << std::endl;

    file.close();

    return 0;
}
```

